import React, { Component } from 'react';
import PropTypes from 'prop-types'
import {BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom'
import withNavigation from './WithNavigation.jsx'
import withParams from './WithParams.jsx'
import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from './AuthenticatedRoute.jsx'

export class FlashCardApp extends Component 
{
  render() 
  {
    const LoginComponentWithNavigation = withNavigation(LoginComponent);
    const HeaderComponentWithNavigation = withNavigation(HeaderComponent);
    const WelcomeComponentWithParams = withParams(WelcomeComponent);
    return (
      <div className="flashCardApp">
        <Router>
          <HeaderComponentWithNavigation/>
          <Routes>
            <Route path="/" element={<LoginComponentWithNavigation />} />
            <Route path="/login" element={<LoginComponentWithNavigation />} />
            <Route path="/logout" element={<LogoutComponent />} />
            <Route path="/welcome/:username" element={
              <AuthenticatedRoute> 
                <WelcomeComponentWithParams />
              </AuthenticatedRoute> 
              } />
            <Route path="/welcome/:username" element={
              <AuthenticatedRoute> 
                <WelcomeComponentWithParams />
              </AuthenticatedRoute> 
              } />
            <Route path="/todo" element={
              <AuthenticatedRoute> 
                <ListTodosComponent />
              </AuthenticatedRoute> 
              } />
            <Route path="*" element={<ErrorComponent />} />
          </Routes>
          <FooterComponent/>
        </Router>
      </div>
    );
  }
}

class HeaderComponent extends Component
{
  //<h1>Flash Card Application</h1>
  render() {
    const isUserLoggedIn = AuthenticationService.isUserLoggedIn();
    //console.log(isUserLoggedIn && "logged in")
    return (
      <div>
        <header>
          <nav className="navbar navbar-expand-md navbar-dark bg-dark">
            <div><a href="127.0.0.1" className="navbar-brand">Brand</a></div>
            <ul className="navbar-nav">
              {isUserLoggedIn && <li><Link className="nav-link" to="/welcome/username">Home</Link></li>}
              {isUserLoggedIn && <li><Link className="nav-link" to="/todo">Todo</Link></li>}
            </ul>
            <ul className="navbar-nav navbar-collapse justify-content-end">
              {!isUserLoggedIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
              {isUserLoggedIn && <li><Link className="nav-link" onClick={AuthenticationService.logout} to="/logout">Logout</Link></li>}
            </ul>
          </nav>
        </header>
      </div>
    )
  }
}
class FooterComponent extends Component
{
  render() {
    return (
      <footer className="footer">
        <span className="text-muted">notes</span>
      </footer>
    )
  }
}

class LogoutComponent extends Component
{
  render() {
    return (
      <>
        <h1>You are logged out</h1>
        <div>
          Exit
        </div>
      </>
    )
  }
}

class WelcomeComponent extends Component
{
  render() {
    return (
      <>
        <h1>welcome header</h1>
        <div className="container">
          Welcome {this.props.params.username}
        </div>
      </>
    );
  }
}

function ErrorComponent() {
  return <div>Error - Page not found</div>
}

class LoginComponent extends Component
{
  constructor(props) {
    super(props);
    this.state = {
      username : 'username',
      password : '',
      hasLoginFailed : false,
    }

    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
  }

  handleChange(event) {
    //console.log(event.target.value)
    //console.log(this.state)
    this.setState(
      {
        [event.target.name] : event.target.value
      }
    )
  }

  loginClicked() {
    //console.log(this.state)
    if (this.state.username==='username' && this.state.password==='a')
    {
      AuthenticationService.registerSuccessfulLogin(this.state.username, this.state.password)

      //console.log("login")
      this.props.navigate(`/welcome/${this.state.username}`)
    }
    else
    {
      this.setState({hasLoginFailed : true})
    }
  }

  render() {
    return (
      <div>
        <h1>Login</h1>
        User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
        Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
        <button className="btn" onClick={this.loginClicked}>Login</button>
        {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Login</div>}
      </div>
    )
  }
}

class ListTodosComponent extends Component
{
  constructor(props){
    super(props)
    this.state = {
      todo : 
      [
        {id: 1, description : 'Todo description', done:false, targetDate: new Date()},
        {id: 2, description : 'Todo description2', done:false, targetDate: new Date()},
        {id: 3, description : 'Todo description3', done:false, targetDate: new Date()}
      ]
    }
  }

  render() {
    //this.state.todo.forEach (entry => console.log(entry.description) );

    return (
      <div>
        <h1>List Todos</h1>
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">id</th>
                <th scope="col">description</th>
                <th scope="col">done</th>
                <th scope="col">targetDate</th>
              </tr>
            </thead>
            <tbody>
              {
                this.state.todo.map (
                  entry => 
                    <tr>
                      <th scope="row">{entry.id}</th>
                      <td>{entry.description}</td>
                      <td>{entry.done.toString()}</td>
                      <td>{entry.targetDate.toString()}</td>
                    </tr>
                )
              }
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}

