import React, { Component } from 'react';
import PropTypes from 'prop-types'
import './Calculator.css'

export class CalculatorComponent extends Component 
{
  constructor()
  {
    super();
    this.state = {
      counter : 0,
      secondCounter : 100
    }

    this.increment = this.increment.bind(this);
  }

  // auto bind
  // increment = () => {

  increment(amount)
  {
    console.log('increment');
    this.setState({
      counter: this.state.counter + 1
    });
  }

  render() 
  {
    return (
      <div className="calculatorComponent">
        <div className="container">
          <div className="row">
            <div role="group" className="border btn-group">
                <IncrementButton incAmount={1} incrementMethod={this.increment}/>
                <button onClick={this.increment} type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button onClick={this.increment} type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button onClick={this.increment} type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
            </div>
          </div>
          <div className="row">
            <div role="group" className="border btn-group">
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
            </div>
          </div>
          <div className="row">
            <div className="border btn-group">
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
                <button type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
            </div>
          </div>
          <div className="row">
            <span className="calc-total">{this.state.counter}</span>
          </div>
        </div>
      </div>
    );
  }
}

export class IncrementButton extends Component 
{
  constructor()
  {
    super();
    this.state = {
      counter : 0
    }

    this.incrementFunction = this.props.incMethod
  }

  // auto bind
  // increment = () => {

  incrementFunction()
  {
    this.setState({
      counter: this.state.counter + this.props.incAmount
    });
  }

  render() 
  {
    return (
      <div className="incrementButton">
        <button onClick={this.incrementFunction} type="button" className="btn btn-secondary btn-lg calc-button">+1</button>
        <div className="container">
          <div className="row">
            <div role="group" className="border btn-group">
              <button 
                onClick={this.adder} 
                type="button" 
                className="btn btn-secondary btn-lg inc-button"
              >
                +{this.props.incAmount}
              </button>
            </div>
          </div>
            <span className="calc-total">{this.state.counter}</span>
        </div>
      </div>
    );
  }
}

IncrementButton.defaultProps = {
  incAmount : 1
};

IncrementButton.propTypes = {
  incAmount : PropTypes.number
};

export default CalculatorComponent;
