import React, { Component } from 'react';
import PropTypes from 'prop-types'
import {BrowserRouter as Router, Route} from 'react-router-dom'

export class FlashCardApp extends Component 
{
  render() 
  {
    return (
      <div className="flashCardApp">
        <h1>Flash Card Application</h1>
        <Router>
          <>
          <Route path="/" component={LoginComponent}/>
          <Route path="/login" component={LoginComponent}/>
          <Route path="/welcome" component={LoginComponent}/>
          </>
        </Router>
        <LoginComponent/>
      </div>
    );
  }
}

class LoginComponent extends Component
{
  constructor(props) {
    super(props);
    this.state = {
      username : 'username',
      password : '',
      hasLoginFailed : false,
      showLoginSuccess : false,
    }

    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
  }

  handleChange(event) {
    //console.log(event.target.value)
    //console.log(this.state)
    this.setState(
      {
        [event.target.name] : event.target.value
      }
    )
  }

  loginClicked() {
    //console.log(this.state)
    if (this.state.username==='username' && this.state.password==='a')
    {
      this.setState({hasLoginFailed : false})
      this.setState({showLoginSuccess : true})
    }
    else
    {
      this.setState({hasLoginFailed : true})
      this.setState({showLoginSuccess : false})
    }
  }

  render() {
    return (
      <div>
        {this.state.hasLoginFailed && <div>Invalid Login</div>}
        {this.state.showLoginSuccess && <div>Login Successful</div>}
        User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
        Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
        <button onClick={this.loginClicked}>Login</button>
      </div>
    )
  }
}

